    public static int solve(ArrayList<Integer> A, int W) {
        int max = 0;
        for(int i : A)
            max = Math.max(max, i);

        int left = 0;
        int right = max;

        return findH(left, right, A, W);
    }

    public static int findH(int left, int right, ArrayList<Integer> A, int W){
        while (left <= right){
            int mid = left + (right-left)/2;
            int sum = sumDifference(A, mid);
            if(left == right) System.out.println("Mid: "+mid+"\nSum: "+sum);

            if(sum > W){
                left = mid + 1;
            } else if (sum < W){
                right = mid - 1;
            } else return mid;
        }
        return 0;
    }

    public static int sumDifference(ArrayList<Integer> A, int mid){
        int sum = 0;

        for(int i : A)
            if(i-mid > 0) sum += (i-mid);

        return sum;
    }
